import '../WeatherShow/WeatherShow.css'

export default class WeatherShow extends Component {
    constructor(props) {
        super()
        this.state = {
            cities: []
        }
    }

    addCity = (data) => {
        this.state.cities.push(data)
        console.log(this.state.cities)
    }

    render() {
        console.log(`печатаю ${this.props.newCity()}`)
        return (
            this.state.cities.map((item, index)=>{
                return ( 
                <div className = 'Weather'>
                    <div className = 'city'>
                        <h3>Город:</h3>
                    </div>
                    <div className = 'city'>
                        <h3>Текущая температура:</h3>
                    </div>
                </div>
                )
            })
        )
    }
}
