import React, {Component} from 'react';
import '../WeatherShow/WeatherShow.css'
import FetchOpenWeather from '../FetchOpenWeather/FetchOpenWeather'
import AirTemperature from '../AirTemperature/AirTemperature';

export default class WeatherShow extends Component {

    render() {
        if (Array.isArray(this.props.cities)) {
            return (
                this.props.cities.map((item, index)=>{
                    return ( 
                    <div key ={index} className = 'Weather'>
                        <div className = 'city'>
                            <h3>{item}</h3>
                        </div>
                        <div className = 'temp'>
                            <h3>Температура:<AirTemperature city = {item}/></h3>

                        </div>
                    </div>
                    )
                })
            )}  
            else
                console.log('Массив городов пуст')
                return null;
    }
}
