import React, {Component} from 'react';
import Wrapper from './Wrapper/Wrapper';
import WeatherShow from './WeatherShow/WeatherShow'
import InputCity from './InputCity/InputCity'
import FetchOpenWeather from './FetchOpenWeather/FetchOpenWeather'


export default class App extends Component {

  componentDidMount() {
    FetchOpenWeather()
  }

  addCity = (callback)=>callback

  render() {
    return (
      <Wrapper>
        <WeatherShow newCity = {this.city}/>
        <InputCity addCity = {this.addCity}/>
      </Wrapper>
    )
  }
}
