import React, { Component } from 'react';
import axios from 'axios';

class Сontrollers extends Component {
  FetchOpenWeather = (cityName) => {
    this.setState({...this.state, isFetching: true})
    const apiKey = 'b6907d289e10d714a6e88b30761fae22';
    axios.get(`http://openweathermap.org/data/2.5/weather?q=${cityName}&appid=${apiKey}&units=metric`)
      .then(response => this.setState({temp: response.data.main.temp, 
        isFetching: false}))
        .catch(e => console.log(e));
  }
}


export default Сontrollers;
