import React, {Component} from 'react';
import '../InputCity/InputCity.css'
import WeatherShow from '../WeatherShow/WeatherShow'

export default class InputCity extends Component {
    constructor(props) {
        super()
        this.state = {
            cities: [],
            newCity:{
                city: ''
            }
        }
    }

    addCityHandler = () => {
        this.setState(prevState => ({
            cities: [
            ...prevState.cities, this.state.newCity.city
            ]
        }),
        () => console.log(this.state.cities))
    }

    addCityHandler2 = ()=>{
        this.props.addCity(this.state.newCity.city)
    }

    inputCityHandler = (event) => {
        let value = event.target.value;
        this.setState(
            prevState => ({
                newCity: {
                ...prevState.newCity,
                city: value
                }
            })
            // () => console.log(this.state.newCity)
        );
    };

    render() {
        return (
            <div className = 'weather_city'>
                <div>
                    <h4>В каком городе узнать погоду:</h4>
                    <input onChange = {this.inputCityHandler} placeholder={"Введите название города"} type="text"/>
                    <button onClick = {this.addCityHandler2}>Создать виджет</button>
                </div>
            </div>
        )
    }
}
