import { Component } from 'react';
import axios from 'axios';

export default class Сontrollers extends Component {
  constructor(props) {
    super(props)
    this.state ={
      isFetching: true,
      newTemp:''
    }
  }

  static FetchOpenWeather = (cityName) => {
    console.log('Метод запущен')
    // this.setState({...this.state, isFetching: true})
    const apiKey = 'b6907d289e10d714a6e88b30761fae22';
    axios.get(`http://openweathermap.org/data/2.5/weather?q=${cityName}&appid=${apiKey}&units=metric`)
      .then(response => this.setState({newTemp: response.data.main.temp, 
        isFetching: false}))
        .catch(e => console.log(e));
  }
}
