import React, {Component} from 'react';
import Wrapper from './Wrapper/Wrapper';
import WeatherShow from './WeatherShow/WeatherShow'
import InputCity from './InputCity/InputCity'
import FetchOpenWeather from './FetchOpenWeather/FetchOpenWeather'


export default class App extends Component {

  constructor(props) {
    super()
    this.state = {
      cities: ''
    }
  }

  componentDidMount() {
    FetchOpenWeather()
  }

  newCity = (userCity)=>{
    this.setState(prevState => ({
        cities: userCity
    }),
    () => console.log(`Данные добавленые в главный стейт ${this.state.cities}`))
  }
  addCity = (addCity)=>addCity

  render() {
    return (
      <Wrapper>
        <WeatherShow newCity = {this.newCity}/>
        <InputCity newCity = {this.newCity}/>
      </Wrapper>
    )
  }
}
