import React, {Component} from 'react';
import '../InputCity/InputCity.css'

export default class WeatherCity extends Component {
    constructor(props) {
        super()
        this.state = {
            cities: [],
            newCity:{
                city: ''
            },
            action: false,
        }
    }

    createWidgetHandler = () => {
        console.log(this.state.newCity)
        this.setState(()=> this.state.cities.push(this.state.newCity.city))
        // this.state.cities.push(this.state.newCity.city)
        console.log(this.state.cities)
        this.setState(state => ({
            action: !state.action
            }));
    }

    addCity = () => {
        console.log(this.state.newCity)
        this.state.cities.push(this.state.newCity.city)
        console.log(this.state.cities)
    } 

    createCityHandler = (event) => {
        let value = event.target.value;
        this.setState(
            prevState => ({
                newCity: {
                ...prevState.newCity,
                city: value
                }
            }),
            () => console.log(this.state.newCity),
            this.props.city(this.state.newCity.city)
        );
    };

    render() {
        return (
            <div className = 'weather_city'>
                <div>
                    <h4>В каком городе узнать погоду:</h4>
                    <input onChange = {this.createCityHandler} placeholder={"Введите название города"} type="text"/>
                    <button onClick = {this.createWidgetHandler}>Создать виджет</button>
                </div>
            </div>
        )
    }
}
